import { useContext } from 'react'
import { Header } from './components/Header'
import { ProductList } from './components/ProductList'
import EcommerceContext from './context/ecommerceContext'
import { Container } from './styled-components/Grid'
import { Loading } from './styled-components/Loading'
import { Alert } from './styled-components/Alert'

const App = () => {
  const ecommerceContext = useContext(EcommerceContext)
  const { loading, error, data } = ecommerceContext

  return (
    <>
      <Header></Header>
      <Container>
        {loading
          ? <Loading>Loading data...</Loading>
          : data ? <ProductList {...data}></ProductList> : <Alert error>Ups, looks like there is an error</Alert>
        }
        {
          error && <Alert error>Ups, looks like there is an error</Alert>
        }
      </Container>
    </>
  );
}

export default App;
