import { useEffect, useState } from 'react';
import { useMutation, useQuery } from '@apollo/client';
import { Items } from './../model';
import { Button } from '../styled-components/Button';
import { Card, CardBottom  } from '../styled-components/Card';
import { ADD_ITEM_TO_ORDER } from './../graphql/mutations';
import { ACTIVE_ORDER } from './../graphql/queries';

interface Props {
  item: Items
}

export const ProductCard = ({item}: Props): JSX.Element => {
  const { name, description, variants, assets } = item
  const [productId, setProductId] = useState('0')
  const [ addToOrder ] = useMutation(ADD_ITEM_TO_ORDER, {
    refetchQueries: [ { query: ACTIVE_ORDER } ]
  })

  addToOrder({ variables: {productId} })

  const { loading, error, data } = useQuery(ACTIVE_ORDER)
    console.log(data)

  const handleOnClick = () => {
    setProductId(variants[0].id)
  }

  return (
    <Card>
      <img src={assets[0].source} alt={name} />
      <h2>{name}</h2>
      <p>{description}</p>
      <CardBottom>
        <h4>${variants[0].priceWithTax}</h4>
        <Button onClick={handleOnClick}>Buy</Button>
      </CardBottom>
    </Card>
  )
}
