import { gql } from '@apollo/client'

export const PRODUCTS = gql`
    query {
        products {
            items {
                id
                name
                description
                assets {
                    source
                    width
                }
                variants {
                    id
                    priceWithTax
                }
            }
        }
    }
`

export const ACTIVE_ORDER = gql`
    query {
        activeOrder {
        state
        }
  }
`
