import styled from 'styled-components'

export const Card = styled.article `
    transition: all 0.3s;
    background-color: #fff;
    box-shadow: 0 1px 5px rgb(0 0 0 / 20%);
    border-radius: 5px;
`

export const CardBottom = styled.div `
    padding: 10px 15px
`
