import { createContext } from 'react'
import { ApolloError } from 'apollo-server-errors';

const defaultValue = {
    loading: false,
    error: new ApolloError('Invalid argument value'),
    data: null
}

const ecommerceContext = createContext(defaultValue)

export default ecommerceContext
