import { useState } from 'react';
import { useMutation } from '@apollo/client';
import { Items } from './../model';
import { Button } from '../styled-components/Button';
import { Card, CardBottom  } from '../styled-components/Card';
import { ADD_ITEM_TO_ORDER } from './../graphql/mutations';

interface Props {
  item: Items
}

export const ProductCard = ({item}: Props): JSX.Element => {
  const { name, description, variants } = item
  const [productId, setProductId] = useState('0')
  const [ addItemToOrder ] = useMutation(ADD_ITEM_TO_ORDER)
  addItemToOrder({ variables: {productId} })

  const handleOnClick = () => {
    setProductId(variants[0].id)
  }

  return (
    <Card>
      <img src='' alt='descrition?'/>
      <h2>{name}</h2>
      <p>{description}</p>
      <CardBottom>
        <h4>${variants[0].priceWithTax}</h4>
        <Button onClick={handleOnClick}>Buy</Button>
      </CardBottom>
    </Card>
  )
}
