import { useReducer } from 'react'
import EcommerceContext from './ecommerceContext'
import EcommerceReducer from './ecommerceReducer'
import { useQuery } from '@apollo/client'
import { PRODUCTS } from '../graphql/queries'
import { GET_PRODUCTS } from './types'

const EcommerceState = (props: any) => {

    const initialState = {
        products: null
    }

    const [state, dispatch] = useReducer(EcommerceReducer, initialState)

    dispatch({ type: GET_PRODUCTS, payload: useQuery(PRODUCTS) })


    return <EcommerceContext.Provider value={{
        products: state.products
    }}>
        {props.children}
    </EcommerceContext.Provider>
}

export default EcommerceState
