import { gql } from '@apollo/client'

export const ADD_ITEM_TO_ORDER = gql `
mutation {
  addItemToOrder(productVariantId: 3 quantity: 1) {
    ...Order {
      productVariant {
        id
        name
      }
      unitPrice
      quantity
      totalPrice
      subTotal
    }
  }
}
`
